import "./ExpenseItem.css";
function ExpenseItem(props) {
  return (
    <div className="expense-item">
      <DateBadge
        month={props.date.getMonth()}
        day={props.date.getDay()}
        year={props.date.getFullYear()}
      />
      <div className="expense-item__description">
        <h2>{props.title}</h2>
        <div className="expense-item__price">${props.amount}</div>
      </div>
    </div>
  );
}

function DateBadge(props) {
  const month = props.month.toLocaleString("en-US", { month: "long" });
  const day = props.day.toLocaleString("en-US", { day: "2-digit" });
  const year = props.year.toLocaleString("en-US", { year: "long" });
  return (
    <div>
      <div>{month}</div>
      <div>{day}</div>
      <div>{year}</div>
    </div>
  );
}

export default ExpenseItem;
